mapper.select:
SELECT movie.*, movie3.audi_acc
FROM movie
INNER JOIN movie3 ON movie.kobis_id = movie3.movie_cd
WHERE movie.genre = #{childSelectValue}
ORDER BY movie3.audi_acc DESC
LIMIT 5;

