mapper.select:
SELECT movie.*, movie3.audi_acc
FROM movie
INNER JOIN movie3 ON movie.kobis_id = movie3.movie_cd
WHERE movie.genre = #{childSelectValue}
ORDER BY movie3.audi_acc DESC
LIMIT 5;

mapper.insert:
INSERT INTO twoweek_movie_vote_list (movie_cd, tmdb_id, kobis_id, kor_name, eng_name, sponsor, release_date, running_time, watch_grade, movie_intro, poster_url, logo_url, main_img_url, release_nation, genre) SELECT movie.id, movie.tmdb_id, movie.kobis_id, movie.kor_name, movie.eng_name, movie.sponsor, movie.release_date, movie.running_time, movie.watch_grade, movie.movie_intro, movie.poster_url, movie.logo_url, movie.main_img_url, movie.release_nation, movie.genre FROM movie INNER JOIN movie3 ON movie.kobis_id = movie3.movie_cd
movie.genre = #{childSelectValue}
YEAR(movie.release_date) = #{childSelectValue}
movie.distributor = #{childSelectValue}
ORDER BY movie3.audi_acc DESC LIMIT 6
